//<![CDATA[
var flgLoad = false;
var menu_height = 38+20+35;
var map = null;
//=================================================================================================
//= initPage
//=================================================================================================
function initPage() {
	resizeMap();
	flgLoad = true;
	window.onresize = function() { resizeMap(); }

	map = new Microsoft.Maps.Map	(
															document.getElementById('map'), 
															{
															credentials: 'Asrn2IMtRwnOdIRPf-7q30XVUrZuOK7K2tzhCACMg7QZbJ4EPsOcLk6mE9-sNvUe',
															showBreadcrumb: true,
															enableSearchLogo: false,
															showMapTypeSelector:false
															}
														);
		//showMapTypeSelector:false
	map.setView({ zoom: 2, center: new Microsoft.Maps.Location(25, 0) })

	var offset = new Microsoft.Maps.Point(0, 5); 
	var pushpinOptions = {text : '', visible: true, textOffset: offset}; 
	var pushpin= new Microsoft.Maps.Pushpin(new Microsoft.Maps.Location(25, 0), pushpinOptions); 
	//map.setView( {center: new Microsoft.Maps.Location(47.6, -122.33), zoom: 10}); 
	map.entities.push(pushpin); 
	//map.entities.clear(); 

	//Microsoft.Maps.MapTypeId.auto			: automatic, 
	//Microsoft.Maps.MapTypeId.road			: road, 
	//Microsoft.Maps.MapTypeId.aerial			: aerial, 
	//Microsoft.Maps.MapTypeId.birdseye	: birdeye  
	map.setView({mapTypeId : Microsoft.Maps.MapTypeId.road});
}
//=================================================================================================
//= resizeMap
//=================================================================================================
function resizeMap () {
	var window_height = 0;

	if( window.innerHeight) 
	{ 
		window_height = window.innerHeight;            
	} 
	else 
	{ 
		window_height = document.documentElement.offsetHeight;                              
	}

    document.getElementById("map").style.height = (window_height-menu_height) + "px"; 

}
// showMap ============================================================================
function showMap () {

	if (flgLoad == true)
	{
		selectTabMenu (0);
		//Microsoft.Maps.MapTypeId.auto			: automatic, 
		//Microsoft.Maps.MapTypeId.road			: road, 
		//Microsoft.Maps.MapTypeId.aerial			: aerial, 
		//Microsoft.Maps.MapTypeId.birdseye	: birdeye  
		map.setView({mapTypeId : Microsoft.Maps.MapTypeId.road});
	}
}
// showSatellite =========================================================================
function showSatellite () {

	if (flgLoad == true)
	{
		selectTabMenu (1);
		
		//Microsoft.Maps.MapTypeId.auto			: automatic, 
		//Microsoft.Maps.MapTypeId.road			: road, 
		//Microsoft.Maps.MapTypeId.aerial			: aerial, 
		//Microsoft.Maps.MapTypeId.birdseye	: birdeye  
		map.setView({mapTypeId : Microsoft.Maps.MapTypeId.aerial});
	}
}
// showBirdsEye =======================================================================
function showBirdsEye () {
	if (flgLoad == true)
	{
		selectTabMenu (2);
		
		//Microsoft.Maps.MapTypeId.auto			: automatic, 
		//Microsoft.Maps.MapTypeId.road			: road, 
		//Microsoft.Maps.MapTypeId.aerial			: aerial, 
		//Microsoft.Maps.MapTypeId.birdseye	: birdeye  
		map.setView({mapTypeId : Microsoft.Maps.MapTypeId.birdseye});
	}
}
// selectTabMenu ======================================================================
function selectTabMenu (idx_tab) {
	
	var cnt_tab;
	
	if (flgLoad == true)
	{
		for (cnt_tab=0;cnt_tab<3 ;cnt_tab++ )
		{
			if (cnt_tab==idx_tab)
			{
				document.getElementById("tab_" + cnt_tab).style.backgroundColor = "#7BAE48";
				document.getElementById("tab_" + cnt_tab).style.cursor="default"; 
				document.getElementById("tab_" + cnt_tab).style.color="#FFFFFF"; 
			}
			else
			{
				document.getElementById("tab_" + cnt_tab).style.backgroundColor = "#FFFFFF";
				document.getElementById("tab_" + cnt_tab).style.cursor="hand"; 
				document.getElementById("tab_" + cnt_tab).style.color="#396112"; 
			}
		}
	}
}
//]]>
